IF NOT EXISTS (SELECT * FROM sys.databases WHERE name = 'desafio')BEGIN    CREATE DATABASE desafio;    PRINT 'Banco de dados desafio criado.';ENDELSEBEGIN    PRINT 'O banco de dados desafio já existe.';ENDUSE desafio;IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Orders')BEGIN    CREATE TABLE Orders (        orderId INT PRIMARY KEY,        value DECIMAL(10, 2),        creationDate DATETIME    );    PRINT 'Tabela Orders criada.';ENDELSEBEGIN    PRINT 'A tabela Orders já existe.';ENDIF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Items')BEGIN    CREATE TABLE Items (        orderId INT,        productId INT,        quantity INT,        price DECIMAL(10, 2),        FOREIGN KEY (orderId) REFERENCES Orders(orderId)    );    PRINT 'Tabela Items criada.';ENDELSEBEGIN    PRINT 'A tabela Items já existe.';ENDINSERT INTO Orders (orderId, value, creationDate)VALUES    (1, 100.00, '2024-07-07T10:00:00'),    (2, 150.50, '2024-07-06T15:30:00'),    (10, 250.00, '2024-06-28T10:00:00');INSERT INTO Items (orderId, productId, quantity, price)VALUES    (1, 101, 2, 20.00),    (1, 102, 1, 60.00),    (2, 103, 3, 15.50),    (2, 104, 2, 35.25),    (10, 109, 5, 60.00),    (10, 110, 2, 90.00);